"use strict";

var config = require("config");


var FredsConfig = {
    get: function get(configKey) {
        var processEnvKey = configKey.replace(".", "_").toUpperCase();
        if (process.env.hasOwnProperty(processEnvKey)) {
            return process.env[processEnvKey];
        } else {
            return config.get(configKey);
        }
    },

    has: function has(configItem) {
        var processEnvItem = configItem.replace(".", "_").toUpperCase();
        if (process.env.hasOwnProperty(processEnvItem)) {
            return process.env[processEnvItem];
        } else {
            return config.has(configItem);
        }
    }
}

module.exports = FredsConfig;